import "./styles.css";
import React, { useState } from "react";

function FavoriteColor() {
  const [color, setColor] = useState("red");
  const ChangeColor = () => {
    setColor("blue");
  };
  return (
    <>
      <h1>I have color {color}</h1>
      <button onClick={ChangeColor}>ChangeColor</button>
    </>
  );
}

const StateObject = () => {
  const [car, setCar] = useState({
    brand: "Ford",
    model: "Mustang",
    year: 2444,
    color: "red",
  });
  return (
    <>
      <h1>My {car.brand}</h1>
      <p>
        It is a {car.color} {car.model} from {car.year}.
      </p>
    </>
  );
};

export default function App() {
  return (
    <div className="App">
      <h1>Change color</h1>
      <FavoriteColor />
      <StateObject />
    </div>
  );
}
