import "./styles.css";
import React, { useState } from "react";



export default function App() {
  function FavoriteColor() {
    const [color, setColor] = useState("red");

    const ChangeColor = () => {
      setColor("blue");
    };
    return (
      <>
        <h1>I have color {color}</h1>
      </>
    );
  }
  return (
    <div className="App">
      <h1>sdf</h1>
      <FavoriteColor />
      <button type="button" onClick={ChangeColor}>Change color</button>
    </div>
  );
}
