import "./styles.css";
import React, { useState } from "react";

function FavoriteWord() {
  const [word, setWord] = useState("red");
  const ChangeWord = () => {
    setWord("blue");
  };

  return (
    <>
      <h1>I have color {word}</h1>
      <button onClick={ChangeWord}>ChangeColor</button>
    </>
  );
}

const StateObject = () => {
  const [car, setCar] = useState({
    brand: "Ford",
    model: "Mustang",
    year: 2444,
    color: "red",
  });

  const UpdateYear = (previousState) => {
    setCar({ ...previousState, year: 1443 });
  };

  return (
    <>
      <h1>My {car.brand}</h1>
      <p>
        It is a {car.color} {car.model} from {car.year}.
      </p>
      <button onClick={UpdateYear}>UpdateYear</button>
    </>
  );
};

export default function App() {
  return (
    <div className="App">
      <h1>Change color</h1>
      <FavoriteColor />
      <StateObject />
    </div>
  );
}
